ValidationRule.errorConditionFormula:
AND(
  NOT($Profile.Id="00e30000000bvR3"), /*System Administrator*/
  ISNEW(),  
  OR(
   /* ISBLANK(MailingCity),*/
    ISBLANK(FirstName),
  /*  NOT (REGEX(MailingCity, "[a-zA-Z' -.]+")), *//*Fields do not allow numbers and special characters other than hyphen, space, period and apostrophe*/
    NOT (REGEX(FirstName, "[a-zA-Z' -]+")),
    NOT (REGEX(LastName, "[a-zA-Z' -]+")) 
  )
)
