WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/21.0/connection.js")}

if (confirm("Are you sure you want to Move this PS Line Item?"))
{

function right(str, len)
{
return str.substring(str.length-len, str.length)
}

var litemObj = new sforce.SObject("PS_Line_Item__c");
litemObj.Id = '{!PS_Line_Item__c.Id}';
litemObj.Case__c = '00076637';
var result = sforce.connection.update([litemObj]);

if (result[0].success=='false') {
alert(result[0].errors.message);
} else {
location.reload(true);
}

}
else
{
alert("The PS Line Item has NOT been moved.");
}
