CustomField.formula:
IF(ISPICKVAL(SFA_Cap_Time_Horizon__c, "1st renewal only"), "for the first renewal only",
   IF(ISPICKVAL(SFA_Cap_Time_Horizon__c, "Thru 2nd Renewal"), "for the first two renewals",
      IF(ISPICKVAL(SFA_Cap_Time_Horizon__c, "Thru 3rd Renewal"), "for the first three renewals",
         IF(ISPICKVAL(SFA_Cap_Time_Horizon__c, "Perpetual"), "for renewals", 
									   IF(ISPICKVAL(SFA_Cap_Time_Horizon__c, "Other"), "Other", "")
						   )
      )
   )
)
