CustomField.formula:
IF(ISPICKVAL(SBQQ__BillingFrequency__c, "Quarterly"), "Quarterly",
   IF(ISPICKVAL(SBQQ__BillingFrequency__c, "Semiannual"), "Semi-annually",
      IF(ISPICKVAL(SBQQ__BillingFrequency__c, "Quarterly Y1 Only"), "Quarterly",
         IF(ISPICKVAL(SBQQ__BillingFrequency__c, "Semi-annual Y1 Only"), "Semi-annually",
            IF(ISPICKVAL(SBQQ__BillingFrequency__c, "Annual"), "Annually", "")
         )
      )
   )
)
