{!REQUIRESCRIPT("/soap/ajax/29.0/apex.js")}

var oppId,r,i;
oppId = '{!Opportunity.Id}';
i = 0;
do {
   i++;
   response = sforce.apex.execute("ServicesSOW","createFlowRequestWithDefaultFields",{opportunityId: oppId});
} while ( (response == 'HTTP_ERROR') && (i <= 10) );
if (response == 'HTTP_ERROR')
  alert('Could not connect to the Intapp Flow server. Please try again later');
else if (response == 'FEATURE_NOT_AVAILABLE_ERROR')
  alert('This feature is available for pilot users only. For more information, contact Kevin Ehrie at kevin.ehrie@intapp.com');
else
  var w = window.open('https://intappprod.open.intapp.com/app/Intake/Requests/RequestForm.aspx?rid='+response);
