WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/21.0/connection.js")}

if (confirm("Are you sure you want to Discontinue this task?")) 
{ 
var task = new sforce.SObject("Task"); 
task.id = "{!Task.Id}"; 
task.Status = "Discontinued";

var result = sforce.connection.update([task]);

if (result[0].getBoolean("success")) 
{ 
// Refresh window 
window.location.reload(); 
} 
else 
{ 
alert("Error saving event"); 
}

}
else 
{
alert("The task has NOT been updated.");
}
